import { Button, Paper, styled, Table, TableBody, TableCell,  TableHead, TableRow } from '@mui/material'
import React, { useEffect, useState } from 'react'


const Container = styled(Table)({
	width: "90%",
	margin: "1.5rem auto",
});

const THead = styled(TableRow)({
	background: "#000000",
	"& > th": {
		color: "#fff",
		fontSize: "15px",
	},
});

const Tbody = styled(TableRow)({
	"& > td": {
		fontSize: "14px",
	},
});

const AllUsers = () => {

  const [user, setUser] = useState([]);

  useEffect(() => {
    getAllClients();
  }, []);

  const getAllClients = async () => {
		const response = await getU();
		setUser(response.data);
	};

  return (
		<Container>
			<TableHead>
				<THead>
					<TableCell>UserID</TableCell>
					<TableCell>Name</TableCell>
					<TableCell>UserName</TableCell>
					<TableCell>Email</TableCell>
					<TableCell>Phone</TableCell>
					<TableCell>Action</TableCell>
				</THead>
			</TableHead>
			<TableBody>
				{users.map((user) => (
					<Tbody key={user._id}>
						<TableCell>{user._id}</TableCell>
						<TableCell>{user.name}</TableCell>
						<TableCell>{user.username}</TableCell>
						<TableCell>{user.email}</TableCell>
						<TableCell>{user.phone}</TableCell>
						<TableCell>
							<Button variant="contained" style={{ marginRight: 10 }} component={Link} to={`/edit/${user._id}`}>
								Edit
							</Button>
							<Button variant="contained" color="secondary" onClick={() => deleteUserDetails(user._id)}>
								Delete
							</Button>
						</TableCell>
					</Tbody>
				))}
			</TableBody>
		</Container>
	);
}

export default AllUsers
