import React from "react";
import { useEffect, useStatee } from "react";
import { CgProfile } from "react-icons/cg";
import { validateUser } from "../services/api";
import "./mix.css";

const defaultUser = {
	email: "",
	name: "",
	id: "",
};

const Dashboard = () => {
	// const [userData, setUserData] = useState(defaultUser);

	const handleLogout = () => {
		localStorage.removeItem("userToken");
		alert("User Logged Out Successfully");
		window.location.reload();
	};

	const isAuthenticated = async () => {
		const token = localStorage.getItem("userToken");
		// console.log(token);
		const { data } = await validateUser(token);

		// setUserData(res.data.user);

		// if(data?.user){
		//   setUserData(data.user);
		// }

		if (data.status == 401 || !data) {
			alert("Please Login to continue");
			window.location.href = "/login";
			console.log("Error Page Redirected");
		}

		console.log(data);
	};

	useEffect(() => {
		isAuthenticated();
	}, []);

	return (
		<>
			<div className="centered-container">
				<div className="centered-content">
					<CgProfile size="10rem" />
					<div className="details">
						<h1>Name: Rahul Kumar Saw</h1>
						<h1>Email: rahulsaw2003@gmail.com</h1>
					</div>
				</div>
				<button className="btn" onClick={(e) => handleLogout()}>
					Logout
				</button>
			</div>
		</>
	);
};

export default Dashboard;
