import React from "react";
import { useEffect, useState } from "react";
import { CgProfile } from "react-icons/cg";
import { validateUser } from "../services/api";
import "./mix.css"



const Dashboard = () => {

  const [user, setUser] = useState({});

  const handleLogout = () => {
    localStorage.removeItem("userToken");
    alert("User Logged Out Successfully");
    window.location.reload();
  };

  const isAuthenticated = async() =>{
    const token = localStorage.getItem("userToken");
    // console.log(token);
    const res = await validateUser(token);

    console.log(res.data.user);
    setUser()
  }

  useEffect(() => {
    isAuthenticated(); 
  }, []);

	return (
		<>
			<div className="centered-container">
				<div className="centered-content">
					<CgProfile size="10rem" />
					<div className="details">
						<h1>Name: Rahul Kumar Saw</h1>
						<h1>Email: rahulsaw2003@gmail.com</h1>
					</div>
				</div>
				<button className="btn" onClick={(e) => handleLogout()}>Logout</button>
			</div>
		</>
	);
};

export default Dashboard;
