import axios from "axios";

const serverURL = "http://localhost:5000";

export const userLogin = async (user) => {
	try {
		const response = axios.post(`${serverURL}/api/login`, user);
        return console.log("User Login Successfully", response);

	} catch (error) {
		console.log("Error in userLogin API: ", error);
		throw error;
	}
};

export const userRegister = async (user) => {
	try {
		await axios.post(`${serverURL}/api/register`, user);
		return console.log("User Registered Successfully", user);
	} catch (error) {
		console.log("Error in userRegister API: ", error);
		throw error;
	}
};
