import React, { useContext } from "react";
import { useEffect } from "react";
import { CgProfile } from "react-icons/cg";
import { useNavigate } from "react-router-dom";
import { validateUser } from "../services/api";
import { LoginContext } from "./Context/Context";
import "./mix.css";

const Dashboard = () => {
	const { loginData, setLoginData } = useContext(LoginContext);
  const history = useNavigate();
  console.log("Hey", loginData);

	const handleLogout = () => {
		localStorage.removeItem("userToken");
		alert("User Logged Out Successfully");
		history("/");
	};

	const isAuthenticated = async () => {
		const token = localStorage.getItem("userToken");
		// console.log(token);
		const { data } = await validateUser(token);

		setLoginData(data);
	};

	useEffect(() => {
		isAuthenticated();
	}, []);

	return (
		<>
			<div className="centered-container">
				<div className="centered-content">
					<CgProfile size="8rem" />
					<div className="details">
						<h1>Name: {loginData.name}</h1>
						<h1>Email: {loginData.email}</h1>
						<h1>UserID: {loginData._id}</h1>
					</div>
				</div>
				<button className="btn" onClick={(e) => handleLogout()}>
					Logout
				</button>
			</div>
		</>
	);
};

export default Dashboard;
