import React, { useContext, useState } from "react";
import { addClient } from "../services/api";
import { LoginContext } from "./Context/Context";

const defaultClient = {
	name: "",
	email: "",
	phone: "",
	organization: "",
};

const AddUser = () => {
	const [userData, setUserData] = useState(defaultClient);
	const { loginData, setLoginData } = useContext(LoginContext);

	const handleValueChange = (e) => {
		setUserData({ ...userData, [e.target.name]: e.target.value });
		// console.log(userData);
	};

	const handleClick = async (e) => {
		e.preventDefault();
		if(loginData?._id){
            const token = localStorage.getItem("userToken");
            const { data } = await addClient(userData, token);
        }
	};
	return (
		<section>
			<div className="form_data">
				<div className="form_heading">
					<h1>Add New Client</h1>
				</div>

				<form method="post">
					<div className="form_input">
						<label htmlFor="name">Name</label>
						<input type="name" name="name" id="name" placeholder="Enter full name" onChange={(e) => handleValueChange(e)} />
					</div>
					<div className="form_input">
						<label htmlFor="email">Email</label>
						<input type="email" name="email" id="email" placeholder="Enter email address" onChange={(e) => handleValueChange(e)} />
					</div>
					<div className="form_input">
						<label htmlFor="text">Email</label>
						<input type="phone" name="phone" id="phone" placeholder="Enter Phone Number" onChange={(e) => handleValueChange(e)} />
					</div>
					<div className="form_input">
						<label htmlFor="organization">Email</label>
						<input type="text" name="organization" id="organization" placeholder="Enter Organization" onChange={(e) => handleValueChange(e)} />
					</div>

					<button className="btn" onClick={(e) => handleClick(e)}>
						Add Client
					</button>
				</form>
			</div>
		</section>
	);
};

export default AddUser;
