import React, { useState } from "react";
import "./mix.css";
import { userRegister } from "../services/api";



const Register = () => {
	const [userData, setUserData] = useState("");
	const [passShow, setPassShow] = useState(false);
	const [cpassShow, setcPassShow] = useState(false);

	const handleValueChange = (e) => {
		setUserData({ ...userData, [e.target.name]: e.target.value });
	};

	const handleClick = async () => {
		await userRegister(userData);
	};

	return (
		<section>
			<div className="form_data">
				<div className="form_heading">
					<h1>Regsiter Now</h1>
					<p>Hi, we are glad that you are back. Proceed to Registration. </p>
				</div>

				<form action="">
					<div className="form_input">
						<label htmlFor="name">Name</label>
						<input type="name" name="name" id="name" placeholder="Enter your full name" />
					</div>
					<div className="form_input">
						<label htmlFor="email">Email</label>
						<input type="email" name="email" id="email" placeholder="Enter your email address" />
					</div>
					<div className="form_input">
						<label htmlFor="password">Password</label>
						<div className="two">
							<input type={!passShow ? "password" : "text"} name="password" id="password" placeholder="Enter your password" />
							<div className="showpass" onClick={() => setPassShow(!passShow)}>
								{!passShow ? "Show" : "Hide"}
							</div>
						</div>
					</div>
					<div className="form_input">
						<label htmlFor="cpassword">Confirm Password</label>
						<div className="two">
							<input type={!cpassShow ? "password" : "text"} name="cpassword" id="cpassword" placeholder="Re-enter your Password" />
							<div className="showpass" onClick={() => setcPassShow(!cpassShow)}>
								{!cpassShow ? "Show" : "Hide"}
							</div>
						</div>
					</div>
					<button className="btn">Sign Up</button>
					<p>Don't have an account? Login</p>
				</form>
			</div>
		</section>
	);
};

export default Register;
